import React, {useState} from 'react';
import {Menu} from "antd";
import {  AppstoreOutlined, SettingOutlined, UserAddOutlined, UserOutlined, LogoutOutlined, } from '@ant-design/icons';
import {Link} from"react-router-dom";
import firebase from "firebase";
import {useDispatch, useSelector} from "react-redux";
import{useHistory} from "react-router-dom";
import "./header.css"

const { SubMenu, Item } = Menu;

const Header = () => {
  const [current, setCurrent] = useState("home");

  let dispatch = useDispatch();
  let { user } = useSelector((state) => ({ ...state }));

  let history = useHistory();

  const handleClick = (e) => {
    // console.log(e.key);
    setCurrent(e.key);
  };

  const logout = () => {
    firebase.auth().signOut();
    dispatch({
      type: "LOGOUT",
      payload: null,
    });
    history.push("/login");
  };

return (

    <Menu  className="menu menu-dark bg-secondary" onClick={handleClick} theme="dark" mode="horizontal">
      <Item key="home" icon={<AppstoreOutlined />}>
       <Link to="/">Home</Link>
      </Item>

      {!user && (
        <Item key="register" icon={<UserAddOutlined />} className="float-right">
        <Link to="/register">Register</Link>
        </Item>
      )}


      {!user && (
      <Item key="login" icon={<UserOutlined />} className="float-right">
      <Link to="/login">Login</Link>

      </Item>
      )}

      {user && 
      <SubMenu key="SubMenu" 
      icon={<SettingOutlined />}
       title={user.email && user.email.split("@")[0]}
       className="float-right"
       >

       {user && user.role === "subscriber" &&  
         <Item> 
          <Link to="/user/history">Dashboard</Link>
        </Item>}

        {user && user.role === "admin" &&  
         <Item> 
          <Link to="/admin/dashboard">AdminDashboard</Link>
        </Item>}

          <Item icon={<LogoutOutlined/>} onClick={logout} >Logout</Item>


      </SubMenu>
      }
    </Menu>

    );


}
export default Header ;
